mapper.select:
select

from bs_archive t


		and t.ar_code = #{arCode}			


		and t.ar_name = #{arName}			


		and t.ar_pinyin = #{arPinyin}

select

from bs_archive t
where t.id = #{value}

mapper.update:
update bs_archive
ar_code = #{arCode}, ar_name = #{arName}, ar_pinyin = #{arPinyin}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate}, is_deleted = #{isDeleted}, status = #{status},
where id = #{id}
