mapper.select:
select

from bs_defect t


		and t.defect_name = #{defectName}			


		and t.defect_name_en = #{defectNameEn}			


		and t.defect_type_id = #{defectTypeId}

select

from bs_defect t
where t.id = #{value}

mapper.update:
update bs_defect
defect_name = #{defectName}, defect_name_en = #{defectNameEn}, defect_type_id = #{defectTypeId}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate}, is_deleted = #{isDeleted}, status = #{status}, remark = #{remark},
where id = #{id}
