mapper.select:
select

from bs_item t


		and t.item_name = #{itemName}			


		and t.workline_id = #{worklineId}			


		and t.workshop_id = #{workshopId}			


order by workshop_id ,workshop_id

select

from bs_item t
where t.id = #{value}

mapper.update:
update bs_item
item_name = #{itemName}, workline_id = #{worklineId}, workshop_id = #{workshopId}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate}, is_deleted = #{isDeleted}, status = #{status}, remark = #{remark},
where id = #{id}
