mapper.select:
select

from bs_material t


		and t.material_code = #{materialCode}			


		and t.material_name = #{materialName}			


		and t.material_type = #{materialType}

select

from bs_material t
where t.id = #{value}

mapper.update:
update bs_material
material_code = #{materialCode}, material_name = #{materialName}, material_price = #{materialPrice}, material_type = #{materialType}, material_ts = #{materialTs}, material_unit = #{materialUnit}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate}, is_deleted = #{isDeleted}, status = #{status},
where id = #{id}
