mapper.select:
select

from bs_shift t


		and t.shift_name = #{shiftName}			


		and t.shift_manager = #{shiftManager}			


		and t.workshop_id = #{workshopId}			


order by workshop_id

select

from bs_shift t
where t.id = #{value}

mapper.update:
update bs_shift
shift_name = #{shiftName}, shift_manager = #{shiftManager}, workshop_id = #{workshopId}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate}, is_deleted = #{isDeleted}, status = #{status}, remark = #{remark},
where id = #{id}
