mapper.select:
select

from bs_workshop t
where t.id = #{value}

mapper.update:
update bs_workshop
workshop_name = #{workshopName}, workshop_manager = #{workshopManager}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate}, is_deleted = #{isDeleted}, status = #{status}, remark = #{remark},
where id = #{id}
